import Vue from 'vue';  // import Vue
import Router from 'vue-router';  // import Vue Router

Vue.use(Router);  // ใช้งาน Vue Router

export default new Router({
  mode: 'history',
  routes: [
    {
      path: '/',
      name: 'Home',
      component: () => import('./components/Home.vue')  // ใช้ dynamic import สำหรับ component
    },
    {
      path: '/about',
      name: 'About',
      component: () => import('./components/About.vue')
    }
  ]
});
